import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Row, Col, Button } from 'react-bootstrap';
import { fetchHabitDetails, updateHabitProgress } from '../services/api';
import { Chart } from "react-google-charts";

function HabitDetail() {
  const [habit, setHabit] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    loadHabitDetails();
  }, [id]);




  const loadHabitDetails = useCallback(async () => {
    try {
      const data = await fetchHabitDetails(id);
      setHabit(data);
    } catch (error) {
      console.error('Error loading habit details:', error);
    }
  }, [id]);

  useEffect(() => {
    loadHabitDetails();
  }, [loadHabitDetails]);





  const handleMarkComplete = async () => {
    try {
      await updateHabitProgress(id, { completed: true });
      loadHabitDetails();
    } catch (error) {
      console.error('Error updating habit progress:', error);
    }
  };

  if (!habit) return <div>Loading...</div>;

  const chartData = [
    ['Date', 'Completion'],
    ...habit.progress.map(p => [new Date(p.date), p.completed ? 1 : 0])
  ];

  return (
    <Container>
      <h1>{habit.name}</h1>
      <p>Frequency: {habit.frequency}</p>
      <Row>
        <Col md={8}>
          <Chart
            width={'100%'}
            height={'400px'}
            chartType="Calendar"
            loader={<div>Loading Chart</div>}
            data={chartData}
            options={{
              title: `${habit.name} Completion`,
            }}
          />
        </Col>
        <Col md={4}>
          <Button onClick={handleMarkComplete}>Mark as Complete</Button>
        </Col>
      </Row>
    </Container>
  );
}

export default HabitDetail;
