import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

console.log('API_URL:', API_URL);

const api = axios.create({
  baseURL: API_URL,
  withCredentials: true,
});

// Request interceptor for adding the auth token
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
}, (error) => Promise.reject(error));

// User related API calls
export const getUserData = () => api.get('/user').then(res => res.data);

// Habit related API calls
export const fetchHabits = () => api.get('/habits').then(res => res.data);
export const createHabit = (habitData) => api.post('/habits', habitData).then(res => res.data);
export const fetchHabitDetails = (id) => api.get(`/habits/${id}`).then(res => res.data);
export const updateHabitProgress = (id, progressData) => api.post(`/habits/${id}/progress`, progressData).then(res => res.data);
export const updateHabit = (id, habitData) => api.put(`/habits/${id}`, habitData).then(res => res.data);
export const deleteHabit = (id) => api.delete(`/habits/${id}`).then(res => res.data);

// Statistics API call
export const getHabitStatistics = () => api.get('/habits/statistics').then(res => res.data);

// Error handling
api.interceptors.response.use(
  (response) => response,
  (error) => {
    console.error('API Error:', error.response);
    if (error.response && error.response.status === 401) {
      // Unauthorized, clear token and redirect to login
      localStorage.removeItem('token');
      window.location = '/login';
    }
    return Promise.reject(error);
  }
);

export default api;
