import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Button, Alert } from 'react-bootstrap';
import HabitList from './HabitList';
import HabitForm from './HabitForm';
import { fetchHabits, createHabit } from '../services/api';
import { Chart } from "react-google-charts";

function Dashboard() {
  const [habits, setHabits] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadHabits();
  }, []);

  const loadHabits = async () => {
    try {
      const data = await fetchHabits();
      setHabits(data);
      setError(null);
    } catch (error) {
      console.error('Error loading habits:', error);
      setError('Failed to load habits. Please try again.');
    }
  };

  const handleCreateHabit = async (habitData) => {
    try {
      await createHabit(habitData);
      setShowForm(false);
      loadHabits();
      setError(null);
    } catch (error) {
      console.error('Error creating habit:', error);
      setError('Failed to create habit. Please try again.');
    }
  };

  const chartData = [
    ['Habit', 'Completion Rate'],
    ...habits.map(habit => [habit.name, habit.completionRate || 0])
  ];

  return (
    <Container>
      <h1>Your Habits</h1>
      {error && <Alert variant="danger">{error}</Alert>}
      <Row>
        <Col md={8}>
          <HabitList habits={habits} onHabitUpdate={loadHabits} />
          <Button onClick={() => setShowForm(!showForm)} className="mt-3">
            {showForm ? 'Cancel' : 'Add New Habit'}
          </Button>
          {showForm && <HabitForm onSubmit={handleCreateHabit} />}
        </Col>
        <Col md={4}>
          <Chart
            width={'100%'}
            height={'300px'}
            chartType="PieChart"
            loader={<div>Loading Chart</div>}
            data={chartData}
            options={{
              title: 'Habit Completion Rates',
            }}
          />
        </Col>
      </Row>
    </Container>
  );
}

export default Dashboard;
